def compare(n1, n2):    return True if int(n1 + n2) > int(n2 + n1) else Falsedef sort(array):    if not array:        return []    left, right = [], []    pivot = array[0]    [left.append(i) if compare(i, pivot) else right.append(i) for i in array[1:]]    return sort(left) + [pivot] + sort(right)def solution(numbers):    string_numbers = [str(i) for i in numbers]    result = sort(string_numbers)    return str(int("".join(result)))if __name__ == "__main__":    numbers = [1, 10, 11, 101, 110]    print(solution(numbers))